definition.script:
pipeline {
    agent any

    stages {
        stage('Build') {
            steps {
                // Instalação das dependências
                sh 'npm install'
                // Build da aplicação
                sh 'npm run build'
            }
        }
        stage('Test') {
            steps {
                // Execução dos testes
                sh 'npm test'
            }
        }
        stage('Deploy') {
            steps {
                // Implantação da aplicação
                sh 'npm deploy'
            }
        }
    }
}
